package com.gemini.audioplayer

import android.os.Bundle
import android.webkit.WebView
import androidx.appcompat.app.AppCompatActivity

/**
 * Base Tauri Activity
 * This is a placeholder - the actual implementation will be generated by Tauri
 */
open class TauriActivity : AppCompatActivity() {
    
    protected var webView: WebView? = null
    
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        // Tauri will inject WebView initialization here
    }
    
    protected fun evaluateJavascript(script: String, callback: ((String?) -> Unit)?) {
        webView?.evaluateJavascript(script, callback)
    }
}
